mapper.select:
SELECT m_id id, m_email email
FROM member 
WHERE m_id = #{id} AND m_passwd = #{passwd}

SELECT m_id id, m_name name, m_addr1 addr1, m_addr2 addr2, m_phone phone, m_birth birth, m_gender gender, m_email email
FROM member
WHERE m_id = #{id}

mapper.update:
UPDATE member
SET m_name = #{name}, m_passwd = #{passwd}, m_birth=#{birth}, m_addr1=#{addr1}, m_addr2=#{addr2}, m_phone=#{phone}, m_email=#{email}
WHERE m_id = #{id}
